import Mario from  "./images/Mario.png";
import DonkeyKong from  "./images/DonkeyKong.png";
import Link from  "./images/Link.png";
import Samus from  "./images/Samus.png";
import DarkSamus from  "./images/Dark-Samus.png";
import Yoshi from  "./images/Yoshi.png";
import Kirby from "./images/Kirby.png";
import Fox from "./images/Fox.png";
import Pikachu from "./images/Pikachu.png";
import Luigi from  "./images/Luigi.png";
import Ness from "./images/Ness.png";

import Captain from  "./images/CaptainFalcon.png";
import Banjo from "./images/Banjo.png";
import Byleth from "./images/Byleth.png";


export const characters = [
    {
        id: 1,
        name: "Mario",
        series: "Super Mario",
        images: Mario,
        description: "Mario is a short middleweight with relatively balanced attributes, possessing average walking speed and air acceleration, above average dashing speed and weight, while his falling speed and gravity are slightly below average relative to the cast. However, he also has high air speed (tied with Sonic, Donkey Kong, Little Mac and Inkling for the 12th fastest) and jump height, but in exchange for poor range and low traction. "
    },

    {
        id: 2,
        name: "Donkey Kong",
        series: "Donkey Kong",
        images: DonkeyKong,
        description: "Donkey Kong is unsurprisingly a large super heavyweight, having the same weight value as King Dedede, and is the third-heaviest character in the game behind Bowser and King K. Rool. Despite this, he is one of the faster heavyweights in the game: his ground speed, aerial movement and attack speed are all quite fast, as he has the 9th fastest walking speed and the 28th fastest dashing speed, above average falling speed and gravity, and is tied with Mario, Sonic, Little Mac, and Inkling for the 12th fastest air speed, but in contrast has below average air acceleration."
    },

    {
        id: 3,
        name: "Link",
        series: "Legend of Zelda",
        images: Link,
        description: "Link is a heavyweight swordsman that sports powerful attacks and long range via the Master Sword. Link sports an above-average walking speed, the 15th slowest dashing speed, above-average initial dash, is tied with Diddy Kong for the 10th slowest air speed, the 4th slowest air acceleration, above-average gravity and falling speed, the 6th highest fast falling speed, and above-average traction. Lastly, Link has among the lowest jump heights in the game. Overall, these stats render Link's mobility generally poor."
    },

    {
        id: 4,
        name: "Samus",
        series: "Metroid",
        images: Samus,
        description: "Samus has historically been a lower-ranking character in the Smash Bros. series, with most of her incarnations (except Melee and post-patch SSB4) infamously considered low tier in their respective games. Even after being heavily buffed via patches, at the end of SSB4's metagame, Samus ended up as a low-mid tier character (tied with Robin for 37th out of 55). Likely as a result of this, Samus has been significantly buffed overall in the transition to Ultimate."
    },

    {
        id: 5,
        name: "Dark Samus",
        series: "Metroid",
        images: DarkSamus,
        description: "As with certain other Echo Fighters, Dark Samus is nearly identical gameplay-wise to Samus, with largely aesthetic differences. However, she has many basic animations that are different from Samus, a more standard roll which does not involve turning into a Morph Ball, electric effects instead of fire effects for most of Samus's fire-based moves, and different models for both the Morph Ball and Missile."
    },

    {
        id: 6,
        name: "Yoshi",
        series: "Yoshi",
        images: Yoshi,
        description: "Yoshi is a heavyweight fighter with above-average mobility, good frame data, and decent KO potential. He sports a unique shield in the form of a Yoshi Egg, which generally lasts longer than most shields and is larger in size; thus, it does a good job of protecting Yoshi from powerful attacks."
    },

    {
        id: 7,
        name: "Kirby",
        series: "Kirby",
        images: Kirby,
        description: "Kirby is known for being a small lightweight, being tied for the eighth lightest character in the game. True to his franchise, Kirby is a slow, yet floaty character in the air, but has average ground speed. He has the twenty-first slowest walking speed, fortieth fastest dashing speed, and a decently quick initial dash. He also has the fifth slowest air speed, fifteenth highest air acceleration, third lowest gravity, the fourth lowest falling and fast falling speeds, and above average traction."
    },

    {
        id: 8,
        name: "Fox",
        series: "Star Fox",
        images: Fox,
        description: "Fox is a lightweight, hit-and-run type character with excellent grounded mobility and good frame data for the majority of his moveset. Fox is incredibly mobile: he possesses the fifth-fastest dashing speed, third-fastest walking speed, 32nd fastest air speed, 21st highest air acceleration, the highest falling (and thus fast falling) speed and gravity in the game, with the latter two allowing him to quickly move from the air to the ground with ease. These attributes allow him to keep up with almost any opponent while additionally making him very difficult to punish."
    },

    {
        id: 9,
        name: "Pikachu",
        series: "Pokemon",
        images: Pikachu,
        description: "Pikachu is a small lightweight, tied with Kirby, Olimar, and Mewtwo for the seventh lightest fighter. It has overall good mobility, possessing fast walking and dashing speeds, high jumps, is tied for the ninth-highest air acceleration and has the fourth-highest traction, but slightly above average falling speed and gravity and below-average airspeed. Due to its fast mobility, a good projectile in Thunder Jolt, a good frame data and some of the fastest defensive options, Pikachu has a good approach and great neutral game."
    },

    {
        id: 10,
        name: "Luigi",
        series: "Super Mario",
        images: Luigi,
        description: "Luigi is a middleweight who is somewhat floaty and has attributes that deviate from most characters of his weight class. Luigi has average walking and dashing speeds, the second slowest air speed, average air acceleration, slightly below average gravity, slow falling speed and fast falling speed and low traction. Luigi also sports a very high jump and double jump, among the highest in the game. Overall, Luigi is a rather floaty character who is sluggish in some aspects, especially in the air."
    },

    {
        id: 11,
        name: "Ness",
        series: "EarthBound",
        images: Ness,
        description: "Ness has a unique moveset that features elements of various archetypes. While his three projectiles allow for some zoning, he truly excels at short distances, where he can overwhelm opponents with fast aerials, steady shield pressure, and punishing throws. Ness' overall mobility is rather one-sided: on the ground his walking speed, initial dash, and dash speed are below average, but his aerial movement shines thanks to his excellent air acceleration, decent air speed, and above-average jump height."
    },

    //STOPPED HERE


    {
        id: 12,
        name: "Captain Falcon",
        series: "F-Zero",
        images: Captain 
    },


    {
        id: 73,
        name: "Banjo & Kazooie",
        series: "Banjo Kazooie",
        images: Banjo
    },

    {
        id: 75,
        name: "Byleth",
        series: "Fire Emblem",
        images: Byleth
    },



]
